package com.rays.stream;

public class Contestant {

	private String name;
	private String phonNo;

	public Contestant() {
		// TODO Auto-generated constructor stub
	}

	public Contestant(String name, String phoneNo) {

		this.name = name;
		this.phonNo = phoneNo;

	}

	public String getName() {
		return name;
	}

	public String getPhonNo() {
		return phonNo;
	}

}
